import { motion } from "framer-motion";

export default function BlockchainInfo() {
  return (
    <section className="p-10 bg-gradient-to-b from-white to-gray-100 text-gray-900 min-h-screen">
      <motion.h2
        className="text-4xl md:text-5xl font-bold mb-8 text-center"
        initial={{ y: -50, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        ¿Qué es Blockchain?
      </motion.h2>

      <div className="max-w-4xl mx-auto space-y-8">
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.7 }}
          className="bg-white rounded-2xl shadow-lg p-6"
        >
          <h3 className="text-2xl font-semibold mb-2">Definición</h3>
          <p className="text-gray-700">
            Blockchain es una base de datos descentralizada y distribuida que registra transacciones de forma segura,
            transparente y permanente. Cada bloque contiene información que enlaza con el anterior, formando una cadena inalterable.
          </p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.7, delay: 0.2 }}
          className="bg-white rounded-2xl shadow-lg p-6"
        >
          <h3 className="text-2xl font-semibold mb-2">Oportunidades</h3>
          <ul className="list-disc pl-5 space-y-2 text-gray-700">
            <li>Sistemas financieros descentralizados (DeFi)</li>
            <li>Identidad digital soberana</li>
            <li>Contratos inteligentes autoejecutables</li>
            <li>Gestión de la cadena de suministro</li>
          </ul>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7, delay: 0.4 }}
          className="bg-white rounded-2xl shadow-lg p-6"
        >
          <h3 className="text-2xl font-semibold mb-2">Ejemplos reales</h3>
          <p className="text-gray-700">
            Empresas como Ethereum, Solana y Cardano utilizan blockchain para facilitar pagos, NFTs, dApps y sistemas de gobernanza descentralizada.
          </p>
        </motion.div>
      </div>
    </section>
  );
}
