import { motion } from "framer-motion";

export default function HeroSection() {
  return (
    <section className="min-h-screen flex flex-col items-center justify-center bg-black text-white">
      <motion.h1
        className="text-5xl font-bold mb-4"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Bienvenidos
      </motion.h1>

      <motion.img
        src="/logo.png"
        alt="Logo"
        className="w-32 h-32 mb-4"
        initial={{ opacity: 0, scale: 0.5 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, delay: 0.5 }}
      />

      <div className="flex space-x-4">
        <a href="#"><img src="/facebook.png" className="w-6" alt="FB" /></a>
        <a href="#"><img src="/twitter.png" className="w-6" alt="Twitter" /></a>
        <a href="#"><img src="/instagram.png" className="w-6" alt="IG" /></a>
      </div>
    </section>
  );
}
