import { motion } from "framer-motion";

export default function BlockchainInfo() {
  return (
    <section className="p-10 bg-white text-black min-h-screen">
      <motion.h2
        className="text-4xl font-bold mb-4"
        initial={{ x: -100, opacity: 0 }}
        whileInView={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        ¿Qué es Blockchain?
      </motion.h2>

      <motion.p
        className="text-lg"
        initial={{ x: 100, opacity: 0 }}
        whileInView={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        Blockchain es una tecnología descentralizada que permite registrar transacciones
        de manera segura, transparente e inmutable. Abre oportunidades para sistemas
        financieros abiertos, identidad digital, contratos inteligentes y más.
      </motion.p>
    </section>
  );
}
